import React, { useState, useEffect } from 'react';
import SearchBar from './SearchBar';

const StopArrivals = ({ stopId, stopName }) => {
  const [arrivals, setArrivals] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchArrivals = async () => {
      if (!stopId) {
        return;
      }

      try {
        setLoading(true);

        const response = await fetch(`https://api.tfl.gov.uk/StopPoint/${stopId}/Arrivals`);
        const data = await response.json();

        setArrivals(data);
        setLoading(false);
      } catch (error) {
        setError(error);
        setLoading(false);
      }
    };

    fetchArrivals();
  }, [stopId]);

  return (
    <div>
      <h2>Stop Arrivals</h2>
      {stopId && stopName && (
        <p>Selected Station: {stopName} (ID: {stopId})</p>
      )}

      <SearchBar onSearch={(query) => console.log('Searching...', query)} />

      {loading && <p>Loading arrivals...</p>}

      {error && <p>Error fetching arrivals: {error.message}</p>}

      {arrivals.length > 0 && (
        <ul>
          {arrivals.map((arrival) => (
            <li key={arrival.id}>
              <p>Station Name: {stopName}</p>
              <p>Line: {arrival.lineName}</p>
              <p>Destination: {arrival.destinationName}</p>
              <p>Time to Arrival: {arrival.timeToStation} seconds</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default StopArrivals;
